mapper.select:
SELECT  FROM bpm_form_rights WHERE formKey=#{formKey} 

	and (actDefId is null or actDefId='')
	and (NODEID IS NULL OR NODEID ='')

SELECT  FROM bpm_form_rights WHERE actDefId=#{actDefId}
and (NODEID IS NULL OR NODEID ='')
and parentActDefId=#{parentActDefId,jdbcType=VARCHAR} 
and (parentActDefId is null or parentActDefId='')

SELECT  FROM bpm_form_rights WHERE actDefId=#{actDefId}
and parentActDefId=#{parentActDefId,jdbcType=VARCHAR} 
and (parentActDefId is null or parentActDefId='')

SELECT  FROM bpm_form_rights WHERE actDefId=#{actDefId}
and nodeId=#{nodeId}
and parentActDefId=#{parentActDefId,jdbcType=VARCHAR} 
and (parentActDefId is null or parentActDefId='')

mapper.delete:
delete from  bpm_form_rights WHERE formKey=#{formKey,jdbcType=VARCHAR} 

	and (actDefId is null or actDefId='')
	and (NODEID IS NULL OR NODEID ='')

delete from bpm_form_rights WHERE actDefId=#{actDefId}
and (NODEID IS NULL OR NODEID ='')
and parentActDefId=#{parentActDefId,jdbcType=VARCHAR} 
and (parentActDefId is null or parentActDefId='')

delete from bpm_form_rights WHERE actDefId=#{actDefId}
and nodeId=#{nodeId}
and parentActDefId=#{parentActDefId,jdbcType=VARCHAR} 
and (parentActDefId is null or parentActDefId='')
