sql.where:
AND ID  =#{id} 
AND FORM_KEY  LIKE #{formKey}  
AND TABLE_ID  =#{tableId} 
AND TABLE_NAME  LIKE #{tableName}  
AND SUBJECT  LIKE #{subject}  
AND CREATE_BY  =#{createBy} 
AND CREATOR  LIKE #{creator}  
AND CREATE_TIME  =#{createTime} 
AND CREATE_TIME  >=#{begincreateTime,jdbcType=DATE} 
AND CREATE_TIME  <=#{endcreateTime,jdbcType=DATE} 
AND IS_PUBLISHED  =#{isPublished} 
AND PUBLISHER  LIKE #{publisher}  
AND PUBLISH_BY  =#{publishBy} 
AND PUBLISH_TIME  =#{publishTime} 
AND PUBLISH_TIME  >=#{beginpublishTime,jdbcType=DATE} 
AND PUBLISH_TIME  <=#{endpublishTime,jdbcType=DATE} 
AND UPDATOR  LIKE #{updator}  
AND UPDATE_BY  =#{updateBy} 
AND UPDATE_TIME  =#{updateTime} 
AND UPDATE_TIME  >=#{beginupdateTime,jdbcType=DATE} 
AND UPDATE_TIME  <=#{endupdateTime,jdbcType=DATE} 
AND CATEGORY_ID  =#{categoryId} 
and 1 = 1

mapper.select:
SELECT ID,VERSION,FORM_KEY,TABLE_ID,TABLE_NAME,SUBJECT,CREATE_BY,CREATOR,CREATE_TIME,IS_DEFAULT,IS_PUBLISHED,PUBLISHER,PUBLISH_BY,PUBLISH_TIME,UPDATOR,UPDATE_BY,UPDATE_TIME,CATEGORY_ID ,(select count(*) from BPM_MOBILE_FORM_DEF where form_key = a.FORM_KEY) as versionCount FROM BPM_MOBILE_FORM_DEF a and IS_DEFAULT = 1
order by ${orderField} ${orderSeq}
order by ID desc
