mapper.select:
SELECT FROM SYS_MSG_SEND
order by ${orderField} ${orderSeq}
order by sendtime desc


		select 
		   send.id id,send.subject subject, send.userName userName,
		   send.messageType messageType, send.sendTime sendTime, 
		   send.canReply canReply, send.receiverName receiverName, rece.id rid,
		   red.receivetime,send.attachment
		from 
		sys_msg_send send 
		left join sys_msg_read red on send.id=red.messageid and red.RECEIVERID=#{receiverId} , 
		sys_msg_receiver rece
		where 
		   send.id=rece.messageid
		   and (rece.receiverId in (select orgId from SYS_USER_POS where userId = #{receiverId}) 
		          or  rece.receiverid=#{receiverId}
		      )
		 AND send.subject LIKE #{subject} 
         AND send.messageType LIKE #{messageType} 
         AND send.sendTime>=#{beginreceiveTime,jdbcType=TIMESTAMP}
		 AND send.sendTime <=#{endreceiveTime,jdbcType=TIMESTAMP}
         AND red.receiveTime is null  
         AND red.receiveTime is not null 
         GROUP BY rece.MESSAGEID

		order by ${orderField} ${orderSeq}


		order by send.sendtime desc

select send.id id,send.content subject, send.userName userName, send.messageType messageType, send.sendTime sendTime, send.canReply canReply, send.receiverName receiverName, rece.id rid, red.receivetime,send.attachment from sys_msg_send send left join sys_msg_read red on send.id=red.messageid and red.RECEIVERID=#{receiverId} , sys_msg_receiver rece where send.id=rece.messageid and (rece.receiverId in (select orgId from SYS_USER_POS where userId = #{receiverId}) or rece.receiverid=#{receiverId}) AND red.receiveTime is null AND send.subject LIKE #{subject} AND send.messageType LIKE #{messageType} AND send.sendTime>=#{beginreceiveTime,jdbcType=TIMESTAMP} AND send.sendTime <=#{endreceiveTime,jdbcType=TIMESTAMP} AND red.receiveTime is null AND red.receiveTime is not null
order by ${orderField} ${orderSeq}
order by send.sendtime desc

select send.id id,send.subject subject, send.userName userName, send.messageType messageType, send.sendTime sendTime, send.receiverName receiverName, rece.id rid, red.receivetime from (SELECT * from (SELECT * from (SELECT a.id,a.SUBJECT,a.CONTENT,a.USERID,a.USERNAME,a.SENDTIME,a.ID as messageid,a.MESSAGETYPE,a.RECEIVERNAME from sys_msg_send a UNION ALL SELECT b.MESSAGEID as id,'消息回复' SUBJECT,b.CONTENT SUBJECT,b.REPLYID USERID,b.REPLY USERNAME,b.REPLYTIME SENDTIME ,b.MESSAGEID as messageid,7 as MESSAGETYPE,'' as RECEIVERNAME from sys_msg_reply b ) message ORDER BY message.SENDTIME desc) send GROUP BY send.messageid ORDER BY send.sendtime desc) send left join sys_msg_read red on send.id=red.messageid and red.RECEIVERID=#{receiverId} , sys_msg_receiver rece where send.id=rece.messageid and (rece.receiverId in (select orgId from SYS_USER_POS where userId = #{receiverId}) or rece.receiverid=#{receiverId}) AND red.receiveTime is null AND send.subject LIKE #{subject} AND send.messageType LIKE #{messageType} AND send.sendTime>=#{beginreceiveTime,jdbcType=TIMESTAMP} AND send.sendTime <=#{endreceiveTime,jdbcType=TIMESTAMP} AND red.receiveTime is null AND red.receiveTime is not null
order by ${orderField} ${orderSeq}
order by send.sendtime desc



		SELECT DISTINCT a.*  from (
        select  send.*,null as receiverid, '' as receivetime	from 	sys_msg_send send  where send.USERID=#{userId} UNION all 
			(
		      select tt.*,(select re.receivetime from  sys_msg_read re where #{userId}=re.receiverid and re.messageid=tt.id) as receivertime  from (
		                SELECT b.* , rece.receiverid from  sys_msg_send b  LEFT JOIN sys_msg_receiver rece on   b.id = rece.messageid where rece.RECEIVERID=#{userId}
				or (#{userId} in (SELECT USERID from sys_user_pos where ORGID=rece.RECEIVERID))
		      )tt
	         )
			order by sendtime desc
		  ) a
