mapper.select:
SELECT  
from sys_user 
where account=#{account}

SELECT FROM sys_user
order by ${orderField} ${orderSeq}
order by userId desc

		select u.* from sys_user u,sys_user_pos p,sys_org org where u.USERID=p.USERID and p.ORGID=org.ORGID and p.ISPRIMARY=1 
		 AND u.userId  =#{userId} 
		 AND u.fullname  LIKE #{fullname}  
		 AND u.account  LIKE #{account}  
		 AND u.isExpired  =#{isExpired} 
		 AND u.isLock  =#{isLock} 			
            AND u.createtime=#{createtime}
		 AND u.status  =#{status} 
		 AND u.email  LIKE #{email} 
		 AND u.mobile  LIKE #{mobile}  
		 AND u.phone  LIKE #{phone}  
		 AND u.sex  LIKE {sex}  
		 AND u.fromType = #{fromType}  
		 AND u.entrydate LIKE #{entryDate}  
		 AND u.userStatus LIKE #{userStatus}  
		 AND u.hasSyncToWx = #{hasSyncToWx}  
		 AND orgname LIKE #{org}  

		order by ${orderField} ${orderSeq}


			order by u.userId  desc

SELECT distinct a.userId,a.fullname,a.account,a.entrydate,a.userStatus
FROM sys_user a
left join  sys_user_pos b on a.userid = b.userid and b.isdelete = 0
left join sys_org c on b.orgid = c.orgid and c.isdelete = 0
left join sys_user_pos d on b.userid = d.userid and d.isdelete = 0
left join sys_pos e on d.posid = e.posid and e.isdelete = 0 

	 AND userId  =#{userId} 
	 AND fullname  LIKE #{fullname}  
	 AND b.orgid  = #{orgid}  



order by ${orderField} ${orderSeq}


order by userId  desc

		SELECT a.userId,a.fullname,a.account,a.entrydate,a.userStatus,b.userOrgId,c.orgName 
	    FROM  sys_user a 
	    join sys_user_pos b  on  a.userId=b.userId
	    join sys_org c on b.orgId=c.orgId
		where 1=1
		 AND c.path  LIKE '${path}%'  
		 AND a.fullname  LIKE #{fullname}  
		 AND a.account  LIKE #{account}  
         AND a.sex  LIKE #{sex}  
		 AND a.createtime>=#{begincreatetime}
		 AND a.createtime <=#{endcreatetime}

		order by ${orderField} ${orderSeq}


		order by a.userId  desc

	SELECT 
    FROM sys_user 
	where isLock=0 and status=1 and isExpired=0 
	 AND userId  = #{userId}  
	 AND fullname  LIKE #{fullname}  
	 AND account  LIKE #{account}  
	 AND sex  LIKE #{sex}  
	 AND createtime>=#{begincreatetime}
	 AND createtime <=#{endcreatetime}
	 AND fromType  =#{fromType} 

	order by ${orderField} ${orderSeq}


	order by userId  desc

		SELECT DISTINCT a.*,b.userPosId,b.orgId,b.posId as posId1,b.jobId
	    FROM sys_user a left join sys_user_pos b on a.userid = b.userid and b.ISDELETE=0 and  b.USERPOSID=(select b1.USERPOSID from sys_user_pos b1 where b1.userid=b.userid and b1.isdelete=0 order by ISCHARGE desc LIMIT 1)
		inner join w_user_infomation w on a.userid = w.f_userid
		where 1 = 1 and a.account <> 'admin' and a.status <> -1
		 AND fullname  LIKE #{fullname}  
		 AND account  LIKE #{account}  
		 AND a.userid  LIKE #{userid}  
		 AND  b.orgid  in (select orgid from sys_org where ORGPATHNAME like #{orgname})
		 AND  b.jobId  in (select jobid from sys_job where jobname like #{jobname})
		 AND a.mobile  LIKE #{mobile}  
		 AND a.sex  = #{sex}  
		 AND userstatus in

           	 #{item}


		 AND isLock  =#{isLock} 
		 AND isExpired  =#{isExpired} 
		 AND status  =#{status} 
		 AND createtime>=#{begincreatetime}
		 AND createtime <=#{endcreatetime}
		 AND ENTRYDATE>=#{beginentrytime}
		 AND ENTRYDATE <=#{endentrytime}

		 AND FORMALDATE>=#{beginformaltime}
		 AND FORMALDATE <=#{endformaltime}

		 AND RESIGNATIONDATE>=#{beginresignationtime}
		 AND RESIGNATIONDATE <=#{endresignationtime}
		 AND fromType  =#{fromType} 
		 AND b.orgid  =#{orgId} 
		 AND (b.orgid  =#{exportOrgId} 
				or  exists(select * from sys_org o where o.ORGID=b.orgid  and o.path like concat('%',#{exportOrgPath},'%'))
			)

		 AND b.orgid  in (select orgid from sys_org where path like #{orgPath}) 
		 AND (fullname  LIKE '%${commonFilter}%' or account  LIKE '%${commonFilter}%')
		and w.F_birthday is not null and  date_format(w.F_birthday,'%m') in

           	 #{item}



		order by ${orderField} ${orderSeq}


		order by a.userId  desc

select userid, fullname, mobile FROM
sys_user where userstatus<>'离职' and userid<>'1' and mobile<>''
limit #{indexStart},#{pageSize}

SELECT * from ( SELECT DISTINCT a.*,b.userPosId,b.orgId,b.posId as posId1,b.jobId,w.F_birthday as birthday FROM sys_user a left join sys_user_pos b on a.userid = b.userid and b.ISDELETE=0 inner join w_user_infomation w on a.userid = w.f_userid and w.F_birthday is not null and date_format(w.F_birthday,'%m')=date_format(now(),'%m') where 1 = 1 and a.account <> 'admin' and a.status <> -1 and a.userstatus <> "离职" ) tab group by userid
order by ${orderField} ${orderSeq}
order by CONCAT(YEAR(NOW()),'-',date_format(birthday,'%m-%d')) asc

	SELECT a.userId,a.fullname,a.account,a.password,a.isExpired,a.isLock,a.createtime
	,a.status,a.email,a.mobile,a.phone,a.sex,a.picture,a.entrydate,a.userStatus
    FROM  SYS_USER a ,SYS_USER_ROLE b
    WHERE a.isLock=0 and a.status=1 and a.userId=b.userId
    and b.roleId=#{roleId}

	 AND a.fullname  LIKE #{fullname}  
	 AND a.account  LIKE #{account}  
	 AND a.sex  LIKE #{sex}  
	 AND a.createtime>=#{begincreatetime}
	 AND a.createtime <=#{endcreatetime}

	order by ${orderField} ${orderSeq}


	order by a.userId  desc

	SELECT DISTINCT a.userId,a.fullname,a.account,a.password,a.isExpired,a.isLock
	,a.createtime,a.status,a.email,a.mobile,a.phone,a.sex,a.picture,a.entrydate,a.userStatus
    FROM  SYS_USER a 
    inner join SYS_USER_POS b  on  a.userId=b.userId
    inner join SYS_POS c on b.posId=c.posId
	where a.isLock=0 and a.status=1 and c.nodePath LIKE '${nodePath}%' 
	 AND a.fullname  LIKE #{fullname}  
	 AND a.account  LIKE #{account}  
	 AND a.sex  LIKE #{sex}  
	 AND a.createtime>=#{begincreatetime}
	 AND a.createtime <=#{endcreatetime}

	order by ${orderField} ${orderSeq}


	order by a.userId  desc

	SELECT DISTINCT a.userId,a.fullname,a.account,a.password,a.isExpired,a.isLock
	,a.createtime,a.status,a.email,a.mobile,a.phone,a.sex,a.picture,a.entrydate,a.userStatus
    FROM  SYS_USER a 
    inner join SYS_USER_POS b  on  a.userId=b.userId
    inner join SYS_POS c on b.posId=c.posId
	where a.isLock=0 and a.status=1 and c.posId= #{posId} and b.isdelete =0 
	 AND a.fullname  LIKE #{fullname}  
	 AND a.account  LIKE #{account}  
	 AND a.sex  LIKE #{sex}  
	 AND a.createtime>=#{begincreatetime}
	 AND a.createtime <=#{endcreatetime}

	order by ${orderField} ${orderSeq}


	order by a.userId  desc

	   select a.* from sys_user a where a.isLock = 0 and a.status = 1 and a.userid not in(
         select b.userid from  sys_user_pos b where b.isPrimary=1
     	)

		order by ${orderField} ${orderSeq}


		order by a.userId  desc

select * from ( SELECT DISTINCT a.userId,a.account, a.fullname,a.email,a.mobile,a.phone,a.entrydate,a.userStatus FROM sys_user a inner join sys_user_pos b on a.userId=b.userId and b.isPrimary = 1 inner join sys_org c on b.orgId=c.orgId where a.isLock=0 and a.status=1 and c.path LIKE '${path}%' and b.isdelete=0 AND a.fullname LIKE #{fullname} ) tt
order by ${orderField} ${orderSeq}
order by userId desc

	select * from (		
	SELECT DISTINCT a.userId,a.account,a.fullname,a.email,a.mobile,a.phone,a.entrydate,a.userStatus
    FROM  sys_user a 
    inner join   sys_user_pos b on  a.userId=b.userId
    inner join sys_org c on b.orgId=c.orgId
	where a.isLock=0 and a.status=1 and c.orgId =#{orgId} and b.isdelete =0 
	 AND a.fullname  LIKE #{fullname}  
	 ) tt

	order by ${orderField} ${orderSeq}


	order by userId  desc

select t.userId,t.fullname,t.account from sys_user t ,sys_user_pos a  
where t.userid=a.userid and a.isdelete = 0 and  a.posid in

	${posId}

select u.* from SYS_USER u left join SYS_USER_POS uo on u.USERID=uo.USERID
left join SYS_USER_ROLE ur on u.userid=ur.userid
where uo.orgid=#{orgId} and ur.roleid=#{roleId}

		select u.* from SYS_USER u left join SYS_USER_POS uo on u.USERID=uo.USERID
    	left join sys_user_pos up on u.userid=up.userid
    	where uo.orgid=#{orgId} and up.posid=#{posId}

		select distinct u.*
    from sys_user u,sys_user_pos up,
    sys_user_role ur where 
    u.userId=up.USERID AND up.ISDELETE=0
    and u.USERID=ur.USERID
    and up.ORGID=#{orgId} and ur.ROLEID=#{roleId}

		SELECT DISTINCT u.USERID userId,  u.account account, u.fullname fullname,o.orgName
		 FROM  SYS_USER u 
		LEFT JOIN SYS_USER_POS up  on  u.userId=up.userId and up.ISPRIMARY=1 and ISDELETE =0
		LEFT JOIN SYS_ORG o on up.orgId=o.orgId
	 	 LEFT JOIN (select ID,USER_ID from  ats_attendance_file  af 
	 	) aaf on aaf.USER_ID=u.USERID where aaf.ID is null and status != -1

  		 AND o.orgId  =#{orgId} 
		 AND u.USERID  =#{userId}

	SELECT us.* FROM sys_user us,sys_user_pos b WHERE us.userId = b.userid AND isprimary = 1 AND isdelete = 0 AND userstatus <> '离职'
    and orgid in

		${orgList}
